def counter(a) 
    #Combien y a-t-il de handle dans cette array ?
    puts a.length
end


def little_handle(a)
    #Quel est le handle le plus court de cette liste ?
    puts a.sort_by(&:length)[0]
end


def length_handle(a,x)
    #Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
   l=0
   a.each { |n| 
    if n.length==(x+1) 
        l+=1
    end 
}
puts l
end


def is_upper(b)
    b.match('[A-Z]')
end


def fst_cap(a)
    #Combien commencent par une majuscule (première lettre juste après le @) ?
   fs=0
   a.each { |n| 
    #  n = n.delete_prefix("@")  
      if is_upper(n[1])
        fs+=1
      end
    }
    puts fs
end


def order_by_alphabetic(a)
    #Trie la liste de handle par ordre alphabétique.
    puts a.sort_by!{ |e| e.downcase }
end


def order_by_size(a)
    #Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
    puts a.sort_by(&:length)
end


def search_position(a,handle)
    #Quelle est la position dans l'array de la personne @epenser ?
    find = a.index(handle)     
    puts "#{handle} se trouve à la position #{find} de l'array'"
end


def order_by_size_group(a)
    #Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
    puts a.group_by(&:length).map{|k,v| ["#{v.length}  handles possèdent #{k} caractères (avec le @)"]}
   
end

def menu
    
    puts "1 -- Combien y a-t-il de handles dans cette array ?"
    puts "2 -- Quel est le handle le plus court de cette liste ?"
    puts "3 -- Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
    puts "4 -- Combien commencent par une majuscule (première lettre juste après le @) ?"
    puts "5 -- Trie la liste de handle par ordre alphabétique."
    puts "6 -- Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
    puts "7 -- Quelle est la position dans l'array de la personne @epenser ?"
    puts "8 -- Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
    puts "0 -- Quitter"
    puts "chosissez une question :"
    input_menu=gets.chomp.to_i
    puts "  "
    case input_menu
    when 1
        puts "1 -- Combien y a-t-il de handle dans cette array ?"
        counter(ar)
    when 2
        puts "2 -- Quel est le handle le plus court de cette liste ?"
        little_handle(ar)
    when 3
        puts "3 -- Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
        length_handle(ar,5)
    when 4
        puts "4 -- Combien commencent par une majuscule (première lettre juste après le @) ?"
        fst_cap(ar)
    when 5
        puts "5 -- Trie la liste de handle par ordre alphabétique."
        order_by_alphabetic(ar)
    when 6
        puts "6 -- Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
        order_by_size(ar)
    when 7
        puts "7 -- Quelle est la position dans l'array de la personne @epenser ?"
        search_position(ar,'@epenser')
    when 8
        puts "8 -- Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
        order_by_size_group(ar)
    else
      puts "bye :)"
    end
end

def perform
    menu
end

perform